const Proxy = artifacts.require("Proxy");

contract("Proxy", (accounts) => {
  it("should deploy a Proxy", async () => {
    const proxy = await Proxy.new();
    const instance = await Proxy.deployed();
    assert.ok(instance.address, "失敗");
  });
  it("should proxy a function call", async () => {
    const proxy = await Proxy.new();
    const instance = await Proxy.deployed();
    const result = await instance.verifySignature("0x01", "0x01");
    assert.ok(result, "何これ");
  });
  it("should verify a valid signature", async () => {
    const proxy = await Proxy.new();
    const instance = await Proxy.deployed();

    const msg1Sha256 =
      "97d035e32036a670058f2be4e008a7c56355489750a5da6f2af342db4a968e99"; // sha256("message1")

    const msg1Sig =
      ""; // sign(sha256("message1"), 青木のカードの秘密鍵)

    const mynaPubkeyDer =
      ""; // 青木のカードの公開鍵

    const result = await instance.verifySignature(
      "0x" + mynaPubkeyDer,
      "0x" + msg1Sha256,
      "0x" + msg1Sig
    );
    assert.ok(result, "何これ");
  });
});
